    private MutableLiveData<ArrayList<Song>> songs;
    private MutableLiveData<ArrayList<Song>> favSongs;
    private MediatorLiveData<Song> clickedSong;

    public LiveData<ArrayList<Song>> getSongs() {
        if (songs == null) {
            songs = new MutableLiveData<>();
            loadSongs();
        }
        return songs;
    }

    public MutableLiveData<ArrayList<Song>> setFavList(ArrayList<Song> setFavSongs, boolean isFavorite) {
        if (favSongs == null) {
            favSongs = new MutableLiveData<>();
        }
        if (isFavorite) {
            favSongs.setValue(setFavSongs);
        }
        return favSongs;
    }

    public MutableLiveData<ArrayList<Song>> getFavList() {
        return favSongs;
    }

    public MutableLiveData<ArrayList<Song>> deleteFromfav(Song position, boolean isFavorite) {
        if (!isFavorite) {
            favSongs.getValue().remove(position);
        }
        return favSongs;
    }

    public MediatorLiveData<Song> setClickedSong(Song song) {
        if (clickedSong == null) {
            clickedSong = new MediatorLiveData<>();
        }
        clickedSong.setValue(song);
        return clickedSong;
    }

    public MediatorLiveData<Song> getClickedSong() {
        return clickedSong;
    }

